    // Changed parameter from uint256 to int224
    constructor(int224 _price, bool _fresh) {
        price = _price;
        fresh = _fresh;
    }

    // Changed parameter from uint256 to int224
    function setPrice(int224 _price) external {
        price = _price;
    }

    function setFresh(bool _fresh) external {
        fresh = _fresh;
    }

    // CRITICAL FIX: Changed return type from uint256 to int224
    function read() external view returns (int224, uint32) {
        uint32 timestamp = fresh ? uint32(block.timestamp) : uint32(block.timestamp - 4000);
        return (price, timestamp);
    }
}

contracts/Offering.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@api3/contracts/interfaces/IApi3ReaderProxy.sol";

interface IWRAPEDTOKEN {
    enum PayoutFrequency {
        Daily,
        Monthly,
        Yearly
    }

    function registerInvestment(
        address user,
        uint256 amount,
        PayoutFrequency _payoutFrequency
    ) external;

    function claimPayout() external;
}

interface IEscrow {
    function depositNative(address _offeringContract, address _investor) external payable;
    function depositToken(
        address _offeringContract,
        address _investor,
        address tokenAddr,
        uint256 amount
    ) external;
    function refund(address _offeringContract, address _investor) external; // Also update refund to match Escrow.sol
}

struct InitConfig {
    address saleToken;
    uint256 minInvestment;
    uint256 maxInvestment;
    uint256 startDate;
    uint256 endDate;
    uint256 maturityDate;
    bool autoTransfer;
    uint256 fundraisingCap;
    uint256 tokenPrice;
    address tokenOwner;
    address escrowAddress;
    bool apyEnabled;
    address wrappedTokenAddress;
    address investmentManager;
    address payoutTokenAddress;
    uint256 payoutRate;
    IWRAPEDTOKEN.PayoutFrequency defaultPayoutFrequency;
}

contract Offering is AccessControl, Pausable, ReentrancyGuard {
    IERC20 public saleToken;

    bytes32 public constant TOKEN_OWNER_ROLE = keccak256("TOKEN_OWNER_ROLE");

    uint256 public minInvestment;
    uint256 public maxInvestment;
    uint256 public startDate;
    uint256 public endDate;
    uint256 public maturityDate;
    bool public autoTransfer;

    uint256 public fundraisingCap; 
    uint256 public totalRaised; 
    uint256 public totalPendingTokens;
    bool public isSaleClosed;
    bool public apyEnabled;
    address public wrappedTokenAddress;
    address public investmentManager;
    address public payoutTokenAddress; // New state variable
    uint256 public payoutRate; // New state variable
    IWRAPEDTOKEN.PayoutFrequency public defaultPayoutFrequency; // New state variable
    bool private initialized;

    address public treasury;
    address public escrowAddress;
    uint256 public tokenPrice;

    mapping(address => bool) public whitelistedPaymentTokens;
    mapping(address => uint256) public pendingTokens;
    mapping(address => uint256) public totalInvested;

    mapping(address => address) public tokenOracles; // token => API3 reader proxy

    event Invested(
        address indexed investor,
        address paymentToken,
        uint256 paidAmount,
        uint256 tokensReceived
    );
    event Claimed(address indexed investor, uint256 amount);
    event SaleClosed(uint256 totalRaised);
    event TokenPriceUpdated(uint256 newPrice);
    event PaymentTokenWhitelisted(address indexed token, bool status);
    event OracleSet(address indexed token, address oracle);
    event Rescue(address indexed token, uint256 amount, address indexed to);
    event UnclaimedTokensReclaimed(address indexed to, uint256 amount);

    modifier onlyInvestMentmanager() {
        if (msg.sender != investmentManager)
            revert("Caller is not the investmentManager contract");
        _;
    }

    function initialize(InitConfig memory config) external {
        require(!initialized, "Already initialized");

        require(config.saleToken != address(0), "Invalid sale token");
        require(config.tokenOwner != address(0), "Invalid token owner");
        require(config.escrowAddress != address(0), "Invalid escrow address");
        require(
            config.investmentManager != address(0),
            "Invalid investment manager address"
        );
        require(config.fundraisingCap > 0, "Fundraising cap must be positive");
        require(config.tokenPrice > 0, "Token price must be positive");
        require(config.minInvestment <= config.maxInvestment, "Min > max investment");
        require(
            block.timestamp <= config.startDate,
            "Start date must be in the future"
        );
        require(config.startDate < config.endDate, "Start date must be before end date");
        require(config.payoutTokenAddress != address(0), "Invalid payout token address");

        saleToken = IERC20(config.saleToken);
        minInvestment = config.minInvestment;
        maxInvestment = config.maxInvestment;
        startDate = config.startDate;
        endDate = config.endDate;
        maturityDate = config.maturityDate;
        autoTransfer = config.autoTransfer;
        fundraisingCap = config.fundraisingCap;
        tokenPrice = config.tokenPrice;
        escrowAddress = config.escrowAddress;
        apyEnabled = config.apyEnabled;
        wrappedTokenAddress = config.wrappedTokenAddress;
        investmentManager = config.investmentManager;
        payoutTokenAddress = config.payoutTokenAddress;
        payoutRate = config.payoutRate;
        defaultPayoutFrequency = config.defaultPayoutFrequency;

        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(TOKEN_OWNER_ROLE, config.tokenOwner);

        initialized = true;
    }

    // Emergency pause/unpause, only admin
    function pause() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _pause();
        emit Paused(msg.sender);
    }

    function unpause() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _unpause();
        emit Unpaused(msg.sender);
    }

    /**
     * @dev Invest in the offering. Can be called with native LUMIA or ERC20.
     *      Checks for sale token sufficiency and paused state.
     */
    function invest(
        address paymentToken,
        address investor,
        uint256 paymentAmount
    )
        external
        payable
        nonReentrant
        whenNotPaused
        onlyInvestMentmanager
        returns (uint256)
    {
        require(investor != address(0), "Invalid investor address");
        require(!isSaleClosed, "Sale is closed");
        require(block.timestamp >= startDate, "Sale not started");
        require(block.timestamp < endDate, "Sale ended");
        require(
            whitelistedPaymentTokens[paymentToken],
            "Token not whitelisted"
        );
        require(paymentAmount > 0, "Zero amount");

        uint256 usdValue = getUSDValue(paymentToken, paymentAmount);
        require(usdValue >= minInvestment, "Below min investment");
        require(
            totalInvested[investor] + usdValue <= maxInvestment,
            "Exceeds max investment"
        );
        require(totalRaised + usdValue <= fundraisingCap, "Exceeds cap");

        uint256 tokensToReceive = (usdValue * 1e18) / tokenPrice;
        require(tokensToReceive > 0, "Token amount too low");

        // Update investment tracking
        totalRaised += usdValue;
        totalInvested[investor] += usdValue;

        // Ensure enough sale tokens if auto-transfer mode
        if (autoTransfer) {
            require(
                saleToken.balanceOf(address(this)) >= tokensToReceive,
                "Insufficient sale tokens"
            );
        }

        // Handle payments
        if (paymentToken == address(0)) {
            // Native currency to Escrow
            require(msg.value == paymentAmount, "Incorrect native amount");
            IEscrow(escrowAddress).depositNative{value: msg.value}(address(this), investor);
        } else {
            // ERC20 payment to Escrow
            require(msg.value == 0, "Do not send ETH for token payment");
            IERC20(paymentToken).transferFrom(
                investor,
                address(this),
                paymentAmount
            );
            IERC20(paymentToken).approve(escrowAddress, paymentAmount);
            IEscrow(escrowAddress).depositToken(address(this), investor, paymentToken, paymentAmount);
        }
        // Handle token distribution
        if (autoTransfer) {
            if (apyEnabled) {
                // Wrapped token investment
                require(
                    saleToken.approve(wrappedTokenAddress, tokensToReceive),
                    "Approve failed"
                );
                IWRAPEDTOKEN(wrappedTokenAddress).registerInvestment(
                    investor,
                    tokensToReceive,
                    defaultPayoutFrequency
                );
            } else {
                // Direct transfer of sale token
                require(
                    saleToken.transfer(investor, tokensToReceive),
                    "Auto-transfer failed"
                );
            }
        } else {
            // Pending distribution
            pendingTokens[investor] += tokensToReceive;
            totalPendingTokens += tokensToReceive;
        }

        emit Invested(investor, paymentToken, paymentAmount, tokensToReceive);

        // Close sale if cap reached
        if (totalRaised >= fundraisingCap) {
            isSaleClosed = true;
            emit SaleClosed(totalRaised);
        }
        return tokensToReceive;
    }

    /**
     * @dev Get USD value of a token using API3 oracle. Validates price freshness.
     */
    function getUSDValue(
        address token,
        uint256 amount
    ) internal view returns (uint256 usdValue) {
        address oracle = tokenOracles[token];
        require(oracle != address(0), "Oracle not set");

        (int224 value, ) = IApi3ReaderProxy(oracle).read();
        require(value > 0, "Invalid price");

        uint8 tokenDecimals = token == address(0)
            ? 18 : IERC20Metadata(token).decimals();

        usdValue = (amount * uint256(int256(value)) * 1e18) / (10 ** tokenDecimals) / 1e18;
    }

    /**
     * @dev Set new token price. Only owner. Must be positive.
     */
    function setTokenPrice(
        uint256 newPrice
    ) external onlyRole(TOKEN_OWNER_ROLE) {
        require(newPrice > 0, "Invalid price");
        tokenPrice = newPrice;
        emit TokenPriceUpdated(newPrice);
    }

    /**
     * @dev Whitelist or remove payment tokens. Only owner.
     */
    function setWhitelistedPaymentToken(
        address token,
        bool status
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        whitelistedPaymentTokens[token] = status;
        emit PaymentTokenWhitelisted(token, status);
    }

    /**
     * @dev Set API3 oracle for a token. Only owner.
     */
    function setTokenOracle(
        address token,
        address oracle
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(oracle != address(0), "Invalid oracle address");
        tokenOracles[token] = oracle;
        emit OracleSet(token, oracle);
    }

    /**
     * @dev Remove the oracle for a payment token.
     */
    function removeTokenOracle(
        address token
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(tokenOracles[token] != address(0), "No oracle to remove");
        tokenOracles[token] = address(0);
        emit OracleSet(token, address(0));
    }

    /**
     * @dev Claim tokens after maturity.
     */
    function claimTokens(
        address _investor
    )
        external
        nonReentrant
        whenNotPaused
        onlyInvestMentmanager
        returns (uint256)
    {
        require(block.timestamp >= maturityDate, "Maturity not reached");

        uint256 amount = pendingTokens[_investor];
        require(amount > 0, "No tokens to claim");
        require(
            saleToken.balanceOf(address(this)) >= amount,
            "Insufficient sale tokens"
        );

        pendingTokens[_investor] = 0;
        totalPendingTokens -= amount;
        if (apyEnabled) {
            saleToken.approve(wrappedTokenAddress, amount);
            IWRAPEDTOKEN(wrappedTokenAddress).registerInvestment(
                _investor,
                amount,
                defaultPayoutFrequency
            ); // Added semicolon here
        } else {
            require(
                saleToken.transfer(_investor, amount),
                "Token transfer failed"
            );
        }
        emit Claimed(_investor, amount);
        return amount;
    }

    /**
     * @dev Reclaim unclaimed tokens after maturity. Only owner.
     */
    function reclaimUnclaimedTokens(
        address to
    ) external onlyRole(TOKEN_OWNER_ROLE) nonReentrant {
        require(block.timestamp > maturityDate, "Not matured");
        require(to != address(0), "Invalid address");

        uint256 available = saleToken.balanceOf(address(this)) -
            totalPendingTokens;
        require(available > 0, "No unclaimed tokens");
        require(saleToken.transfer(to, available), "Reclaim failed");
        emit UnclaimedTokensReclaimed(to, available);
    }

    /**
     * @dev Rescue any stuck ERC20 tokens (not saleToken) or Lumia sent by mistake.
     *      Only admin can call.
     */
    function rescueTokens(
        address token,
        uint256 amount,
        address to
    ) external onlyRole(DEFAULT_ADMIN_ROLE) nonReentrant {
        require(to != address(0), "Invalid recipient");
        if (token == address(0)) {
            // Rescue LUMIA
            require(address(this).balance >= amount, "Not enough LUMIA");
            (bool sent, ) = to.call{value: amount}("");
            require(sent, "LUMIA rescue failed");
        } else {
            require(token != address(saleToken), "Cannot rescue saleToken");
            require(
                IERC20(token).balanceOf(address(this)) >= amount,
                "Not enough tokens"
            );
            require(IERC20(token).transfer(to, amount), "ERC20 rescue failed");
        }
        emit Rescue(token, amount, to);
    }

    /**
     * @dev Receive and fallback - revert to prevent accidental LUMIA sending.
     */
    receive() external payable {
        revert("Direct LUMIA not accepted");
    }

    fallback() external payable {
        revert("Fallback not accepted");
    }

    /**
     * @dev Grant TOKEN_OWNER_ROLE to other addresses. Only admin.
     */
    function grantTokenOwner(
        address newOwner
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(newOwner != address(0), "Zero address");
        _grantRole(TOKEN_OWNER_ROLE, newOwner);
    }

    /**
     * @dev Revoke TOKEN_OWNER_ROLE. Only admin.
     */
    function revokeTokenOwner(
        address owner
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(owner != address(0), "Zero address");
        _revokeRole(TOKEN_OWNER_ROLE, owner);
    }

    /**
     * @dev Update investment limits.
     */
    function setInvestmentLimits(
        uint256 _minInvestment,
        uint256 _maxInvestment
    ) external onlyRole(TOKEN_OWNER_ROLE) {
        require(_minInvestment <= _maxInvestment, "Min > max investment");
        minInvestment = _minInvestment;
        maxInvestment = _maxInvestment;
    }

    /**
     * @dev Update end date.
     */
    function setEndDate(
        uint256 _endDate
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(_endDate > block.timestamp, "Invalid end date");
        require(_endDate > endDate, "New end date must be after current");
        require(_endDate < maturityDate, "End date must be before maturity");
        endDate = _endDate;
    }
}

contracts/OfferingFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "./Offering.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./WrapedToken.sol";

// Import the WrapedTokenConfig struct
import {WrapedTokenConfig} from "./WrapedToken.sol";

struct CreateOfferingConfig {
    address saleToken;
    uint256 minInvestment;
    uint256 maxInvestment;
    uint256 startDate;
    uint256 endDate;
    uint256 maturityDate;
    bool autoTransfer;
    bool apyEnabled;
    uint256 fundraisingCap;
    uint256 tokenPrice;
    address tokenOwner;
    address escrowAddress;
    address investmentManager;
    address payoutTokenAddress;
    uint256 payoutRate;
    IWRAPEDTOKEN.PayoutFrequency defaultPayoutFrequency;
}

struct CreateOfferingWithTokensConfig {
    address saleToken;
    uint256 minInvestment;
    uint256 maxInvestment;
    uint256 startDate;
    uint256 endDate;
    uint256 maturityDate;
    bool autoTransfer;
    bool apyEnabled;
    uint256 fundraisingCap;
    uint256 tokenPrice;
    address tokenOwner;
    address escrowAddress;
    address investmentManager;
    address payoutTokenAddress;
    uint256 payoutRate;
    IWRAPEDTOKEN.PayoutFrequency defaultPayoutFrequency;
    address[] paymentTokens;
    address[] oracles;
}

/**
 * @title OfferingFactory
 * @dev Factory contract to deploy and manage Offering contracts.
 */
contract OfferingFactory is Ownable {
    uint256 public count;
    mapping(uint256 => address) public offerings;
    mapping(address => address) public owners;
    mapping(address => uint256[]) public byOwner;

    // USDT configuration
    address public usdtAddress;
    address public usdtOracleAddress;

    event OfferingDeployed(
        uint256 indexed offeringId,
        address indexed creator,
        address indexed offeringAddress,
        address tokenOwner
    );

    event USDTConfigUpdated(
        address indexed usdtAddress,
        address indexed usdtOracleAddress
    );

    constructor() Ownable(msg.sender) {}

    function setUSDTConfig(
        address _usdtAddress,
        address _usdtOracleAddress
    ) external onlyOwner {
        require(_usdtAddress != address(0), "Invalid USDT address");
        require(_usdtOracleAddress != address(0), "Invalid oracle address");

        usdtAddress = _usdtAddress;
        usdtOracleAddress = _usdtOracleAddress;

        emit USDTConfigUpdated(_usdtAddress, _usdtOracleAddress);
    }

    function createOffering(CreateOfferingConfig memory config) external onlyOwner returns (address offeringAddress) {
        require(config.escrowAddress != address(0), "Invalid escrow address");
        require(config.payoutTokenAddress != address(0), "Invalid payout token address");

        address wrappedTokenAddress = address(0);

        Offering offering = new Offering();
        if (config.apyEnabled) {
            WrapedTokenConfig memory wrappedConfig = WrapedTokenConfig({
                name: "Wrapped Token",
                symbol: "WRT",
                peggedToken: config.saleToken,
                payoutToken: config.payoutTokenAddress,
                maturityDate: config.maturityDate,
                payoutRate: config.payoutRate,
                offeringContract: address(offering)
            });
            wrappedTokenAddress = address(new WRAPEDTOKEN(wrappedConfig));
        }

        InitConfig memory initConfig = InitConfig({
            saleToken: config.saleToken,
            minInvestment: config.minInvestment,
            maxInvestment: config.maxInvestment,
            startDate: config.startDate,
            endDate: config.endDate,
            maturityDate: config.maturityDate,
            autoTransfer: config.autoTransfer,
            fundraisingCap: config.fundraisingCap,
            tokenPrice: config.tokenPrice,
            tokenOwner: config.tokenOwner,
            escrowAddress: config.escrowAddress,
            apyEnabled: config.apyEnabled,
            wrappedTokenAddress: wrappedTokenAddress,
            investmentManager: config.investmentManager,
            payoutTokenAddress: config.payoutTokenAddress,
            payoutRate: config.payoutRate,
            defaultPayoutFrequency: config.defaultPayoutFrequency
        });

        offering.initialize(initConfig);
        offeringAddress = address(offering);

        _storeOffering(offeringAddress, config.tokenOwner);
    }

    function createOfferingWithPaymentTokens(CreateOfferingWithTokensConfig memory config) external onlyOwner returns (address offeringAddress) {
        require(config.paymentTokens.length == config.oracles.length, "Array length mismatch");
        require(config.paymentTokens.length > 0, "No payment tokens provided");
        require(config.escrowAddress != address(0), "Invalid escrow address");
        require(config.payoutTokenAddress != address(0), "Invalid payout token address");

        address wrappedTokenAddress = address(0);
        Offering offering = new Offering();

        if (config.apyEnabled) {
            WrapedTokenConfig memory wrappedConfig = WrapedTokenConfig({
                name: "Wrapped Token",
                symbol: "WRT",
                peggedToken: config.saleToken,
                payoutToken: config.payoutTokenAddress,
                maturityDate: config.maturityDate,
                payoutRate: config.payoutRate,
                offeringContract: address(offering)
            });
            wrappedTokenAddress = address(new WRAPEDTOKEN(wrappedConfig));
        }

        InitConfig memory initConfig = InitConfig({
            saleToken: config.saleToken,
            minInvestment: config.minInvestment,
            maxInvestment: config.maxInvestment,
            startDate: config.startDate,
            endDate: config.endDate,
            maturityDate: config.maturityDate,
            autoTransfer: config.autoTransfer,
            fundraisingCap: config.fundraisingCap,
            tokenPrice: config.tokenPrice,
            tokenOwner: config.tokenOwner,
            escrowAddress: config.escrowAddress,
            apyEnabled: config.apyEnabled,
            wrappedTokenAddress: wrappedTokenAddress,
            investmentManager: config.investmentManager,
            payoutTokenAddress: config.payoutTokenAddress,
            payoutRate: config.payoutRate,
            defaultPayoutFrequency: config.defaultPayoutFrequency
        });

        offering.initialize(initConfig);
        offeringAddress = address(offering);

        _configurePaymentTokens(offering, config.paymentTokens, config.oracles);
        _storeOffering(offeringAddress, config.tokenOwner);
    }

    function _configurePaymentTokens(
        Offering offering,
        address[] memory paymentTokens,
        address[] memory oracles
    ) internal {
        for (uint256 i = 0; i < paymentTokens.length; i++) {
            if (paymentTokens[i] != address(0)) {
                require(oracles[i] != address(0), "Invalid oracle for ERC20 token");
            } else {
                require(oracles[i] != address(0), "Invalid oracle for native ETH");
            }
            offering.setTokenOracle(paymentTokens[i], oracles[i]);
            offering.setWhitelistedPaymentToken(paymentTokens[i], true);
        }
    }

    function _storeOffering(address offeringAddress, address tokenOwner) internal {
        offerings[count] = offeringAddress;
        owners[offeringAddress] = msg.sender;
        byOwner[tokenOwner].push(count);

        emit OfferingDeployed(count, msg.sender, offeringAddress, tokenOwner);
        count++;
    }

    function getOfferingAddress(uint256 offeringId) external view returns (address) {
        return offerings[offeringId];
    }

    function getOfferingOwner(address offeringAddress) external view returns (address) {
        return owners[offeringAddress];
    }

    function getOfferingIdsByTokenOwner(address tokenOwner) external view returns (uint256[] memory) {
        return byOwner[tokenOwner];
    }

    function getAllOfferings() external view returns (address[] memory) {
        address[] memory result = new address[](count);
        for (uint256 i = 0; i < count; i++) {
            result[i] = offerings[i];
        }
        return result;
    }

    function getUSDTConfig() external view returns (address usdtToken, address usdtOracle) {
        return (usdtAddress, usdtOracleAddress);
    }
}

contracts/WrapedToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

struct WrapedTokenConfig {
    string name;
    string symbol;
    address peggedToken;
    address payoutToken;
    uint256 maturityDate;
    uint256 payoutRate;
    address offeringContract;
}

contract WRAPEDTOKEN is ERC20, ERC20Burnable, AccessControl, Pausable, ReentrancyGuard {
    enum PayoutFrequency {
        Daily,
        Monthly,
        Yearly
    }

    // Role definitions
    bytes32 public constant PAYOUT_ADMIN_ROLE = keccak256("PAYOUT_ADMIN_ROLE");

    IERC20 public immutable peggedToken;
    IERC20 public immutable payoutToken; // Token used for payouts
    uint256 public immutable maturityDate;
    uint256 public immutable payoutRate; // Payout rate as a percentage (e.g., 100 for 1%)
    uint256 public totalEscrowed;
    address public immutable offeringContract;

    // Emergency unlock settings
    bool public emergencyUnlockEnabled;
    uint256 public emergencyUnlockPenalty; // Penalty percentage (e.g., 1000 = 10%)

    struct Investor {
        uint256 deposited;
        bool hasClaimedTokens;
        uint256 lastPayoutTime;
        uint256 totalPayoutsClaimed;
        PayoutFrequency payoutFrequency;
        uint256 payoutAmountPerPeriod; // Amount to be paid out per period
        uint256 totalPayoutBalance; // Total payout balance available to claim
        bool emergencyUnlocked; // Track if user used emergency unlock
    }

    mapping(address => Investor) public investors;
    uint256 public totalPayoutFunds; // Total payout funds available in contract

    error NoTransfers();
    error InvalidAmt();
    error InvalidToken();
    error Matured();
    error NotMatured();
    error NoDeposit();
    error Claimed();
    error TransferFailed();
    error NoPayout();
    error PeriodNotElapsed();
    error InsufficientFunds();
    error UnlockDisabled();
    error AlreadyUnlocked();
    error InvalidPenalty();
    error InvalidStablecoin();
    error PayoutPeriodNotElapsed();

    event PayoutFundsAdded(uint256 amount, uint256 totalFunds);
    event PayoutClaimed(address indexed user, uint256 amount, uint256 remainingBalance);
    event IndividualPayoutClaimed(address indexed user, uint256 amount);
    event FinalTokensClaimed(address indexed user, uint256 amount);
    event EmergencyUnlockEnabled(uint256 penalty);
    event EmergencyUnlockUsed(address indexed user, uint256 amount, uint256 penalty);

    constructor(WrapedTokenConfig memory config) ERC20(config.name, config.symbol) {
        if (config.peggedToken == address(0) || config.payoutToken == address(0))
            revert InvalidStablecoin();
        peggedToken = IERC20(config.peggedToken);
        payoutToken = IERC20(config.payoutToken);
        maturityDate = config.maturityDate;
        payoutRate = config.payoutRate;
        offeringContract = config.offeringContract;

        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(PAYOUT_ADMIN_ROLE, msg.sender);
    }

    modifier onlyOfferingContract() {
        if (msg.sender != offeringContract)
            revert("Caller is not the offering contract");
        _;
    }

    modifier onlyAfterMaturity() {
        if (block.timestamp < maturityDate) revert NotMatured();
        _;
    }

    function _getPayoutPeriodSeconds(
        PayoutFrequency _frequency
    ) internal pure returns (uint256) {
        if (_frequency == PayoutFrequency.Daily) {
            return 1 days;
        } else if (_frequency == PayoutFrequency.Monthly) {
            return 30 days;
        } else if (_frequency == PayoutFrequency.Yearly) {
            return 365 days;
        }
        return 0;
    }

    function registerInvestment(
        address _user,
        uint256 amount,
        PayoutFrequency _payoutFrequency
    ) external onlyOfferingContract {
        if (amount == 0) revert InvalidAmt();

        bool success = peggedToken.transferFrom(
            offeringContract,
            address(this),
            amount
        );
        if (!success) revert TransferFailed();

        _mint(_user, amount);

        uint256 payoutPerPeriod = (amount * payoutRate) / 10000;

        investors[_user] = Investor({
            deposited: amount,
            hasClaimedTokens: false,
            lastPayoutTime: block.timestamp,
            totalPayoutsClaimed: 0,
            payoutFrequency: _payoutFrequency,
            payoutAmountPerPeriod: payoutPerPeriod,
            totalPayoutBalance: 0, // Will be updated when admin adds payout funds
            emergencyUnlocked: false
        });

        totalEscrowed += amount;
    }

    // Individual payout claim (original functionality)
    function claimPayout() external {
        Investor storage user = investors[msg.sender];
        if (user.deposited == 0) revert NoDeposit();
        if (user.hasClaimedTokens) revert Claimed();

        uint256 payoutPeriodSeconds = _getPayoutPeriodSeconds(user.payoutFrequency);
        if (payoutPeriodSeconds == 0) revert NoPayout();

        uint256 timeElapsed = block.timestamp - user.lastPayoutTime;
        if (timeElapsed < payoutPeriodSeconds) revert PayoutPeriodNotElapsed();

        uint256 periodsPassed = timeElapsed / payoutPeriodSeconds;
        uint256 totalPayoutDue = periodsPassed * user.payoutAmountPerPeriod;

        if (user.totalPayoutsClaimed + totalPayoutDue > user.deposited) {
            totalPayoutDue = user.deposited - user.totalPayoutsClaimed;
        }

        if (totalPayoutDue == 0) revert NoPayout();

        user.lastPayoutTime += periodsPassed * payoutPeriodSeconds;
        user.totalPayoutsClaimed += totalPayoutDue;

        if (!payoutToken.transfer(msg.sender, totalPayoutDue))
            revert TransferFailed();

        emit IndividualPayoutClaimed(msg.sender, totalPayoutDue);
    }

    // Admin adds payout funds and distributes to all investors proportionally
    function addPayoutFunds(uint256 _amount) external onlyRole(PAYOUT_ADMIN_ROLE) nonReentrant {
        if (_amount == 0) revert InvalidAmt();

        // Transfer payout tokens to this contract
        if (!payoutToken.transferFrom(msg.sender, address(this), _amount))
            revert TransferFailed();

        totalPayoutFunds += _amount;

        // Distribute proportionally to all investors based on their wrapped token balance
        uint256 totalSupply = totalSupply();
        if (totalSupply > 0) {
            // Update each investor's payout balance proportionally
            // Note: In practice, you might want to iterate through a list of investors
            // For now, the balance is updated when users claim
        }

        emit PayoutFundsAdded(_amount, totalPayoutFunds);
    }

    // User claims their full available payout balance
    function claimTotalPayout() external nonReentrant {
        Investor storage user = investors[msg.sender];
        if (user.deposited == 0) revert NoDeposit();
        if (user.emergencyUnlocked) revert Claimed();

        // Calculate user's share of total payout funds
        uint256 userBalance = balanceOf(msg.sender);
        if (userBalance == 0) revert NoDeposit();

        uint256 totalSupply = totalSupply();
        uint256 userShare = (totalPayoutFunds * userBalance) / totalSupply;

        // Subtract what user has already claimed
        uint256 availableToClaim = userShare - user.totalPayoutBalance;

        if (availableToClaim == 0) revert NoPayout();

        user.totalPayoutBalance = userShare;

        if (!payoutToken.transfer(msg.sender, availableToClaim))
            revert TransferFailed();

        emit PayoutClaimed(msg.sender, availableToClaim, userShare - availableToClaim);
    }

    // Get user's total available payout balance
    function getUserPayoutBalance(address _user) external view returns (uint256 totalAvailable, uint256 claimed, uint256 claimable) {
        Investor storage user = investors[_user];
        if (user.deposited == 0) return (0, 0, 0);

        uint256 userBalance = balanceOf(_user);
        if (userBalance == 0) return (0, 0, 0);

        uint256 totalSupply = totalSupply();
        if (totalSupply == 0) return (0, 0, 0);

        totalAvailable = (totalPayoutFunds * userBalance) / totalSupply;
        claimed = user.totalPayoutBalance;
        claimable = totalAvailable > claimed ? totalAvailable - claimed : 0;
    }

    // Emergency unlock feature - allows users to unlock tokens before maturity with penalty
    function enableEmergencyUnlock(uint256 _penaltyPercentage) external onlyRole(DEFAULT_ADMIN_ROLE) {
        if (_penaltyPercentage > 5000) revert InvalidPenalty(); // Max 50% penalty
        emergencyUnlockEnabled = true;
        emergencyUnlockPenalty = _penaltyPercentage;
        emit EmergencyUnlockEnabled(_penaltyPercentage);
    }

    function disableEmergencyUnlock() external onlyRole(DEFAULT_ADMIN_ROLE) {
        emergencyUnlockEnabled = false;
        emergencyUnlockPenalty = 0;
    }

    // User can emergency unlock their tokens before maturity (with penalty)
    function emergencyUnlock() external nonReentrant {
        if (!emergencyUnlockEnabled) revert UnlockDisabled();

        Investor storage user = investors[msg.sender];
        if (user.deposited == 0) revert NoDeposit();
        if (user.emergencyUnlocked) revert AlreadyUnlocked();
        if (user.hasClaimedTokens) revert Claimed();

        uint256 wrappedBalance = balanceOf(msg.sender);
        if (wrappedBalance == 0) revert NoDeposit();

        // Calculate penalty
        uint256 penaltyAmount = (user.deposited * emergencyUnlockPenalty) / 10000;
        uint256 amountToReturn = user.deposited - penaltyAmount;

        // Burn wrapped tokens
        _burn(msg.sender, wrappedBalance);

        // Mark as emergency unlocked
        user.emergencyUnlocked = true;

        // Transfer tokens minus penalty
        if (!peggedToken.transfer(msg.sender, amountToReturn))
            revert TransferFailed();

        emit EmergencyUnlockUsed(msg.sender, amountToReturn, penaltyAmount);
    }

    function claimFinalTokens() external onlyAfterMaturity {
        Investor storage user = investors[msg.sender];
        if (user.hasClaimedTokens) revert Claimed();
        if (user.deposited == 0) revert NoDeposit();
        if (user.emergencyUnlocked) revert Claimed();

        uint256 wrappedBalance = balanceOf(msg.sender);
        _burn(msg.sender, wrappedBalance);

        user.hasClaimedTokens = true;
        if (!peggedToken.transfer(msg.sender, user.deposited))
            revert TransferFailed();

        emit FinalTokensClaimed(msg.sender, user.deposited);
    }

    // Emergency pause/unpause
    function pause() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _pause();
    }

    function unpause() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _unpause();
    }

    // Grant payout admin role
    function grantPayoutAdminRole(address _admin) external onlyRole(DEFAULT_ADMIN_ROLE) {
        _grantRole(PAYOUT_ADMIN_ROLE, _admin);
    }

    // Revoke payout admin role
    function revokePayoutAdminRole(address _admin) external onlyRole(DEFAULT_ADMIN_ROLE) {
        _revokeRole(PAYOUT_ADMIN_ROLE, _admin);
    }

    function transfer(address, uint256) public pure override returns (bool) {
        revert NoTransfers();
    }

    function transferFrom(
        address,
        address,
        uint256
    ) public pure override returns (bool) {
        revert NoTransfers();
    }
}

