contracts/InvestmentManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/utils/cryptography/MessageHashUtils.sol";
import "./Offering.sol";
import "./interfaces/IInvestmentManager.sol"; // Import the interface
import "./Escrow.sol"; // Import Escrow to interact with it

contract InvestmentManager is Ownable, IInvestmentManager {
    using ECDSA for bytes32;
    using MessageHashUtils for bytes32;

    address public escrowContract;
    mapping(address => bool) public kybValidators; // Multiple addresses that can sign KYB validations
    uint256 public kybValidatorCount; // Track number of active validators
    mapping(address => bool) public refundsEnabledForOffering;
    mapping(bytes32 => bool) public usedSignatures; // Track used signatures to prevent replay

    event InvestmentRouted(
        address indexed investor,
        address indexed offeringAddress,
        address indexed paymentToken,
        uint256 paidAmount,
        uint256 tokensReceived
    );

    event TokensClaimed(
        address indexed investor,
        address indexed offeringAddress,
        uint256 amount
    );

    event RefundClaimed(
        // New event for subgraph
        address indexed investor,
        address indexed offeringAddress,
        address indexed token,
        uint256 amount
    );

    event refundEnabled(address indexed offeringAddress);

    event KYBValidatorUpdated(
        address indexed oldValidator,
        address indexed newValidator
    );
    event KYBValidatorAdded(address indexed validator);
    event KYBValidatorRemoved(address indexed validator);
    event KYBValidatedInvestment(
        address indexed investor,
        address indexed offeringAddress,
        address indexed paymentToken,
        uint256 paidAmount,
        uint256 tokensReceived,
        bytes32 signatureHash
    );

    constructor() Ownable(msg.sender) {}

    function setEscrowContract(address _escrowContract) external onlyOwner {
        require(
            _escrowContract != address(0),
            "Invalid escrow contract address"
        );
        escrowContract = _escrowContract;
    }

    /**
     * @notice Add a KYB validator address that can sign wallet validations
     * @param _kybValidator Address of the KYB validator to add
     */
    function addKYBValidator(address _kybValidator) external onlyOwner {
        require(_kybValidator != address(0), "Invalid KYB validator address");
        require(!kybValidators[_kybValidator], "Validator already exists");

        kybValidators[_kybValidator] = true;
        kybValidatorCount++;
        emit KYBValidatorAdded(_kybValidator);
    }

    /**
     * @notice Remove a KYB validator address
     * @param _kybValidator Address of the KYB validator to remove
     */
    function removeKYBValidator(address _kybValidator) external onlyOwner {
        require(_kybValidator != address(0), "Invalid KYB validator address");
        require(kybValidators[_kybValidator], "Validator does not exist");
        require(kybValidatorCount > 1, "Cannot remove last validator");

        kybValidators[_kybValidator] = false;
        kybValidatorCount--;
        emit KYBValidatorRemoved(_kybValidator);
    }

    /**
     * @notice Set the initial KYB validator (for backward compatibility)
     * @param _kybValidator Address of the KYB validator (backend signer)
     */
    function setKYBValidator(address _kybValidator) external onlyOwner {
        require(_kybValidator != address(0), "Invalid KYB validator address");
        require(
            kybValidatorCount == 0,
            "Use addKYBValidator for additional validators"
        );

        kybValidators[_kybValidator] = true;
        kybValidatorCount++;
        emit KYBValidatorAdded(_kybValidator);
    }

    /**
     * @notice Verify KYB signature for a wallet
     * @param _wallet Wallet address to validate
     * @param _nonce Unique nonce to prevent replay attacks
     * @param _expiry Signature expiry timestamp
     * @param _signature Off-chain signature from KYB validator
     * @return isValid Whether the signature is valid and not expired
     */
    function verifyKYBSignature(
        address _wallet,
        uint256 _nonce,
        uint256 _expiry,
        bytes memory _signature
    ) public view returns (bool isValid) {
        require(kybValidatorCount > 0, "No KYB validators set");
        require(block.timestamp <= _expiry, "Signature expired");

        // Validate chain ID to prevent cross-chain replay attacks
        uint256 currentChainId = block.chainid;

        // Create message hash
        bytes32 messageHash = keccak256(
            abi.encodePacked(
                "KYB_VALIDATION",
                _wallet,
                _nonce,
                _expiry,
                currentChainId,
                address(this)
            )
        );

        // Additional validation: ensure signature is for current chain
        require(currentChainId != 0, "Invalid chain ID");

        // Convert to Ethereum signed message hash
        bytes32 ethSignedMessageHash = messageHash.toEthSignedMessageHash();

        // Check if signature is already used
        if (usedSignatures[ethSignedMessageHash]) {
            return false;
        }

        // Verify signature
        address recoveredSigner = ethSignedMessageHash.recover(_signature);
        return kybValidators[recoveredSigner];
    }

    /**
     * @notice Route investment with KYB signature validation
     * @param _offeringAddress Address of the offering contract
     * @param _paymentToken Address of payment token (address(0) for ETH)
     * @param _paymentAmount Amount of payment tokens
     * @param _nonce Unique nonce for signature
     * @param _expiry Signature expiry timestamp
     * @param _signature Off-chain KYB validation signature
     */
    function routeInvestmentWithKYB(
        address _offeringAddress,
        address _paymentToken,
        uint256 _paymentAmount,
        uint256 _nonce,
        uint256 _expiry,
        bytes memory _signature
    ) external payable {
        // Verify KYB signature for each investment
        require(
            verifyKYBSignature(msg.sender, _nonce, _expiry, _signature),
            "Invalid KYB signature"
        );

        // Mark signature as used to prevent replay
        bytes32 messageHash = keccak256(
            abi.encodePacked(
                "KYB_VALIDATION",
                msg.sender,
                _nonce,
                _expiry,
                block.chainid,
                address(this)
            )
        );
        bytes32 ethSignedMessageHash = messageHash.toEthSignedMessageHash();
        usedSignatures[ethSignedMessageHash] = true;

        // Proceed with normal investment routing
        Offering offering = Offering(payable(_offeringAddress));
        uint256 tokensReceivedAmount;

        if (_paymentToken == address(0)) {
            tokensReceivedAmount = offering.invest{value: msg.value}(
                _paymentToken,
                msg.sender,
                _paymentAmount
            );
        } else {
            tokensReceivedAmount = offering.invest(
                _paymentToken,
                msg.sender,
                _paymentAmount
            );
        }

        // Emit specialized event for KYB-validated investments
        emit KYBValidatedInvestment(
            msg.sender,
            _offeringAddress,
            _paymentToken,
            _paymentAmount,
            tokensReceivedAmount,
            keccak256(_signature)
        );
    }

    function notifyRefundsEnabled(address _offeringContract) external override {
        require(
            msg.sender == escrowContract,
            "Only Escrow contract can call this function"
        );
        refundsEnabledForOffering[_offeringContract] = true;
        emit refundEnabled(_offeringContract); // Re-emitting the existing event for consistency
    }

    function claimRefund(
        address _offeringContract,
        address _token
    ) external override {
        require(
            refundsEnabledForOffering[_offeringContract],
            "Refunds not enabled for this offering"
        );
        require(_offeringContract != address(0), "Invalid offering contract");
        require(
            _token != address(0) || _token == address(0),
            "Invalid token address"
        ); // Allow address(0) for ETH

        Escrow escrow = Escrow(payable(escrowContract));

        // Get the deposit amount and token from Escrow before calling refund
        Escrow.DepositInfo memory depositInfo = escrow.getDepositInfo(
            _offeringContract,
            msg.sender
        );
        require(depositInfo.amount > 0, "No deposit found for refund");
        require(depositInfo.token == _token, "Token mismatch for refund");

        // Call the refund function on the Escrow contract
        escrow.refund(_offeringContract, msg.sender);

        // Emit event for subgraph with the actual refunded amount and token
        emit RefundClaimed(
            msg.sender,
            _offeringContract,
            depositInfo.token,
            depositInfo.amount
        );
    }

    function routeInvestment(
        address _offeringAddress,
        address _paymentToken,
        uint256 _paymentAmount
    ) external payable {
        // Ensure the offering exists and is valid
        Offering offering = Offering(payable(_offeringAddress));

        uint256 tokensReceivedAmount;
        // If _paymentToken is address(0), it's a native ETH investment
        if (_paymentToken == address(0)) {
            tokensReceivedAmount = offering.invest{value: msg.value}(
                _paymentToken,
                msg.sender,
                _paymentAmount
            );
        } else {
            tokensReceivedAmount = offering.invest(
                _paymentToken,
                msg.sender,
                _paymentAmount
            );
        }

        // Emit event for subgraph
        emit InvestmentRouted(
            msg.sender,
            _offeringAddress,
            _paymentToken,
            _paymentAmount,
            tokensReceivedAmount
        );
    }

    function claimInvestmentTokens(address _offeringAddress) external {
        Offering offering = Offering(payable(_offeringAddress));
        uint256 claimedAmount = offering.claimTokens(msg.sender);
        emit TokensClaimed(msg.sender, _offeringAddress, claimedAmount);
    }

    /**
     * @notice Check if an address is a valid KYB validator
     * @param _validator Address to check
     * @return isValidator Whether the address is a valid KYB validator
     */
    function isKYBValidator(
        address _validator
    ) external view returns (bool isValidator) {
        return kybValidators[_validator];
    }

    /**
     * @notice Get the number of active KYB validators
     * @return count Number of active validators
     */
    function getKYBValidatorCount() external view returns (uint256 count) {
        return kybValidatorCount;
    }

    /**
     * @notice Check if a signature hash has been used
     * @param _signatureHash Hash of the signature to check
     * @return isUsed Whether the signature has been used
     */
    function isSignatureUsed(
        bytes32 _signatureHash
    ) external view returns (bool isUsed) {
        return usedSignatures[_signatureHash];
    }

    // Function to allow the owner to withdraw any accidentally sent ERC20 tokens
    function rescueERC20(
        address _token,
        uint256 _amount,
        address _to
    ) external onlyOwner {
        require(_token != address(0), "Invalid token address");
        require(_amount > 0, "Amount must be greater than 0");
        require(_to != address(0), "Invalid recipient address");
        bool transferSuccess = IERC20(_token).transfer(_to, _amount);
        require(transferSuccess, "ERC20 rescue transfer failed");
    }

    // Function to allow the owner to withdraw any accidentally sent native currency
    function rescueNative(uint256 _amount, address _to) external onlyOwner {
        require(_amount > 0, "Amount must be greater than 0");
        require(_to != address(0), "Invalid recipient address");
        (bool success, ) = payable(_to).call{value: _amount}("");
        require(success, "Native rescue transfer failed");
    }

    receive() external payable {}
}

contracts/Offering.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@api3/contracts/interfaces/IApi3ReaderProxy.sol";

interface IWRAPEDTOKEN {
    enum PayoutFrequency {
        Daily,
        Monthly,
        Yearly
    }

    function registerInvestment(
        address user,
        uint256 amount,
        uint256 usdValue
    ) external;

    function claimPayout() external;
    function setFirstPayoutDate() external;
}

interface IEscrow {
    function depositNative(
        address _offeringContract,
        address _investor
    ) external payable;
    function depositToken(
        address _offeringContract,
        address _investor,
        address tokenAddr,
        uint256 amount
    ) external;
    function enableRefundsByOffering() external;
    function registerOffering(
        address _offeringContract,
        address _offeringOwner
    ) external;
}

struct InitConfig {
    address saleToken;
    uint256 minInvestment;
    uint256 maxInvestment;
    uint256 startDate;
    uint256 endDate;
    uint256 softCap;
    uint256 fundraisingCap;
    uint256 tokenPrice;
    address tokenOwner;
    address escrowAddress;
    bool apyEnabled;
    address wrappedTokenAddress;
    address investmentManager;
    address payoutTokenAddress;
    uint256 payoutRate;
}

contract Offering is AccessControl, Pausable, ReentrancyGuard {
    IERC20 public saleToken;

    bytes32 public constant TOKEN_OWNER_ROLE = keccak256("TOKEN_OWNER_ROLE");

    uint256 public minInvestment;
    uint256 public maxInvestment;
    uint256 public startDate;
    uint256 public endDate;
    uint256 public softCap;

    uint256 public fundraisingCap;
    uint256 public totalRaised;
    uint256 public totalPendingTokens;
    bool public isSaleClosed;
    bool public isOfferingFinalized;
    bool public isOfferingCancelled;
    bool public apyEnabled;
    address public wrappedTokenAddress;
    address public investmentManager;
    address public payoutTokenAddress;
    uint256 public payoutRate;
    IWRAPEDTOKEN.PayoutFrequency public defaultPayoutFrequency;
    bool private initialized;

    address public treasury;
    address public escrowAddress;
    uint256 public tokenPrice;

    /// @dev Maximum allowed price staleness (24 hours)
    uint256 private constant MAX_PRICE_STALENESS = 24 hours;

    mapping(address => bool) public whitelistedPaymentTokens;
    mapping(address => uint256) public pendingTokens;
    mapping(address => uint256) public totalInvested;

    mapping(address => address) public tokenOracles; // token => API3 reader proxy

    event Invested(
        address indexed investor,
        address paymentToken,
        uint256 paidAmount,
        uint256 tokensReceived
    );
    event Claimed(address indexed investor, uint256 amount);
    event SaleClosed(uint256 totalRaised);
    event OfferingFinalized(uint256 timestamp); // New event
    event OfferingCancelled(uint256 timestamp);
    event SoftCapReached(uint256 totalRaised, uint256 softCap);
    event TokenPriceUpdated(uint256 newPrice);
    event PaymentTokenWhitelisted(address indexed token, bool status);
    event OracleSet(address indexed token, address oracle);
    event Rescue(address indexed token, uint256 amount, address indexed to);
    event UnclaimedTokensReclaimed(address indexed to, uint256 amount);

    modifier onlyInvestMentmanager() {
        if (msg.sender != investmentManager)
            revert("Caller is not the investmentManager contract");
        _;
    }

    function initialize(InitConfig memory config) external {
        require(!initialized, "Already initialized");

        require(config.saleToken != address(0), "Invalid sale token");
        require(config.tokenOwner != address(0), "Invalid token owner");
        require(config.escrowAddress != address(0), "Invalid escrow address");
        require(
            config.investmentManager != address(0),
            "Invalid investment manager address"
        );
        require(config.softCap > 0, "Soft cap must be positive");
        require(config.fundraisingCap > 0, "Fundraising cap must be positive");
        require(
            config.softCap <= config.fundraisingCap,
            "Soft cap cannot exceed fundraising cap"
        );
        require(config.tokenPrice > 0, "Token price must be positive");
        require(
            config.minInvestment <= config.maxInvestment,
            "Min > max investment"
        );
        require(
            block.timestamp <= config.startDate,
            "Start date must be in the future"
        );
        require(
            config.startDate < config.endDate,
            "Start date must be before end date"
        );
        require(
            config.payoutTokenAddress != address(0),
            "Invalid payout token address"
        );

        saleToken = IERC20(config.saleToken);
        minInvestment = config.minInvestment;
        maxInvestment = config.maxInvestment;
        startDate = config.startDate;
        endDate = config.endDate;
        softCap = config.softCap;
        softCap = config.softCap;
        fundraisingCap = config.fundraisingCap;
        tokenPrice = config.tokenPrice;
        escrowAddress = config.escrowAddress;
        apyEnabled = config.apyEnabled;
        wrappedTokenAddress = config.wrappedTokenAddress;
        investmentManager = config.investmentManager;
        payoutTokenAddress = config.payoutTokenAddress;
        payoutRate = config.payoutRate;

        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(TOKEN_OWNER_ROLE, config.tokenOwner);

        initialized = true;
    }

    // Emergency pause/unpause, only admin
    function pause() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _pause();
        emit Paused(msg.sender);
    }

    function unpause() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _unpause();
        emit Unpaused(msg.sender);
    }

    /**
     * @dev Finalize the offering. Can only be called after end date by admin or token owner.
     *      Once finalized, users can claim their tokens.
     */
    function finalizeOffering() external {
        require(msg.sender == escrowAddress, "Not authorized to finalize");
        require(block.timestamp >= endDate, "Sale not ended yet");
        require(!isOfferingFinalized, "Already finalized");
        require(!isOfferingCancelled, "Offering is cancelled");

        isOfferingFinalized = true;
        isSaleClosed = true; // Ensure sale is closed when finalized

        // Set first payout date in WrappedToken
        if (apyEnabled && wrappedTokenAddress != address(0)) {
            IWRAPEDTOKEN(wrappedTokenAddress).setFirstPayoutDate();
        }

        emit OfferingFinalized(block.timestamp);
        emit SaleClosed(totalRaised);
    }

    /**
     * @dev Cancel the offering and enable refunds. Only token owner can call.
     *      Can be called anytime before finalization.
     */
    function cancelOffering() external onlyRole(TOKEN_OWNER_ROLE) {
        require(!isOfferingFinalized, "Already finalized");
        require(!isOfferingCancelled, "Already cancelled");

        isOfferingCancelled = true;
        isSaleClosed = true;

        // Enable refunds in escrow
        IEscrow(escrowAddress).enableRefundsByOffering();

        emit OfferingCancelled(block.timestamp);
    }

    /**
     * @dev Invest in the offering. Can be called with native LUMIA or ERC20.
     *      Checks for sale token sufficiency and paused state.
     */
    function invest(
        address paymentToken,
        address investor,
        uint256 paymentAmount
    )
        external
        payable
        nonReentrant
        whenNotPaused
        onlyInvestMentmanager
        returns (uint256)
    {
        require(investor != address(0), "Invalid investor address");
        require(!isSaleClosed, "Sale is closed");
        require(!isOfferingCancelled, "Offering is cancelled");
        require(!isOfferingCancelled, "Offering is cancelled");
        require(block.timestamp >= startDate, "Sale not started");
        require(block.timestamp < endDate, "Sale ended");
        require(
            whitelistedPaymentTokens[paymentToken],
            "Token not whitelisted"
        );
        require(paymentAmount > 0, "Zero amount");

        uint256 usdValue = getUSDValue(paymentToken, paymentAmount);
        require(usdValue >= minInvestment, "Below min investment");
        require(
            totalInvested[investor] + usdValue <= maxInvestment,
            "Exceeds max investment"
        );
        require(totalRaised + usdValue <= fundraisingCap, "Exceeds cap");

        // Additional safety checks for investment limits
        require(usdValue <= type(uint128).max, "Investment amount too large");
        require(
            totalRaised <= type(uint128).max - usdValue,
            "Total raised would overflow"
        );

        uint256 tokensToReceive = (usdValue * 1e18) / tokenPrice;
        require(tokensToReceive > 0, "Token amount too low");
        require(tokensToReceive <= type(uint128).max, "Token amount too large");

        // Update investment tracking
        totalRaised += usdValue;
        totalInvested[investor] += usdValue;

        // Handle payments
        if (paymentToken == address(0)) {
            // Native currency to Escrow
            require(msg.value == paymentAmount, "Incorrect native amount");
            (bool success, ) = escrowAddress.call{value: msg.value}(
                abi.encodeWithSignature(
                    "depositNative(address,address)",
                    address(this),
                    investor
                )
            );
            require(success, "Escrow deposit failed");
        } else {
            // ERC20 payment to Escrow
            require(msg.value == 0, "Do not send ETH for token payment");

            // Check transfer success
            bool transferSuccess = IERC20(paymentToken).transferFrom(
                investor,
                address(this),
                paymentAmount
            );
            require(transferSuccess, "Payment token transfer failed");

            // Check approval success
            bool approvalSuccess = IERC20(paymentToken).approve(
                escrowAddress,
                paymentAmount
            );
            require(approvalSuccess, "Payment token approval failed");

            // Use low-level call for escrow interaction
            (bool success, ) = escrowAddress.call(
                abi.encodeWithSignature(
                    "depositToken(address,address,address,uint256)",
                    address(this),
                    investor,
                    paymentToken,
                    paymentAmount
                )
            );
            require(success, "Escrow token deposit failed");
        }

        pendingTokens[investor] += tokensToReceive;
        totalPendingTokens += tokensToReceive;

        emit Invested(investor, paymentToken, paymentAmount, tokensToReceive);

        // Check if soft cap is reached
        if (totalRaised >= softCap && totalRaised < softCap + usdValue) {
            emit SoftCapReached(totalRaised, softCap);
        }

        // Check if soft cap is reached
        if (totalRaised >= softCap && totalRaised < softCap + usdValue) {
            emit SoftCapReached(totalRaised, softCap);
        }

        // Close sale if cap reached
        if (totalRaised >= fundraisingCap) {
            isSaleClosed = true;
            emit SaleClosed(totalRaised);
        }
        return tokensToReceive;
    }

    /**
     * @dev Get USD value of a token using API3 oracle. Validates price freshness.
     */
    function getUSDValue(
        address token,
        uint256 amount
    ) internal view returns (uint256 usdValue) {
        address oracle = tokenOracles[token];
        require(oracle != address(0), "Oracle not set");

        (int224 value, uint32 timestamp) = IApi3ReaderProxy(oracle).read();
        require(value > 0, "Invalid price");

        // Validate price freshness to prevent stale price exploitation
        require(
            block.timestamp - timestamp <= MAX_PRICE_STALENESS,
            "Price data too stale"
        );

        uint8 tokenDecimals = token == address(0)
            ? 18
            : IERC20Metadata(token).decimals();

        // Use checked arithmetic to prevent overflow
        usdValue =
            (amount * uint256(int256(value)) * 1e18) /
            (10 ** tokenDecimals) /
            1e18;

        // Additional overflow check
        require(usdValue > 0, "USD value calculation overflow");
    }

    /**
     * @dev Whitelist or remove payment tokens. Only owner.
     */
    function setWhitelistedPaymentToken(
        address token,
        bool status
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        whitelistedPaymentTokens[token] = status;
        emit PaymentTokenWhitelisted(token, status);
    }

    /**
     * @dev Set API3 oracle for a token. Only owner.
     */
    function setTokenOracle(
        address token,
        address oracle
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(oracle != address(0), "Invalid oracle address");
        tokenOracles[token] = oracle;
        emit OracleSet(token, oracle);
    }

    /**
     * @dev Remove the oracle for a payment token.
     */
    function removeTokenOracle(
        address token
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(tokenOracles[token] != address(0), "No oracle to remove");
        tokenOracles[token] = address(0);
        emit OracleSet(token, address(0));
    }

    /**
     * @dev Claim tokens after offering is finalized.
     */
    function claimTokens(
        address _investor
    )
        external
        nonReentrant
        whenNotPaused
        onlyInvestMentmanager
        returns (uint256)
    {
        require(isOfferingFinalized, "Offering not finalized yet");
        require(!isOfferingCancelled, "Offering is cancelled");

        require(isOfferingFinalized, "Offering not finalized yet");
        require(!isOfferingCancelled, "Offering is cancelled");

        uint256 amount = pendingTokens[_investor];
        require(amount > 0, "No tokens to claim");
        require(
            saleToken.balanceOf(address(this)) >= amount,
            "Insufficient sale tokens"
        );

        pendingTokens[_investor] = 0;
        totalPendingTokens -= amount;

        if (apyEnabled) {
            saleToken.approve(wrappedTokenAddress, amount);
            uint256 usdValue = (amount * tokenPrice) / 1e18;
            IWRAPEDTOKEN(wrappedTokenAddress).registerInvestment(
                _investor,
                amount,
                usdValue
            );
        } else {
            require(
                saleToken.transfer(_investor, amount),
                "Token transfer failed"
            );
        }
        emit Claimed(_investor, amount);
        return amount;
    }

    /**
     * @dev Reclaim unclaimed tokens after offering is finalized. Only owner.
     */
    function reclaimUnclaimedTokens(
        address to
    ) external onlyRole(TOKEN_OWNER_ROLE) nonReentrant {
        require(isOfferingFinalized, "Offering not finalized");
        require(isOfferingFinalized, "Offering not finalized");
        require(to != address(0), "Invalid address");

        uint256 available = saleToken.balanceOf(address(this)) -
            totalPendingTokens;
        require(available > 0, "No unclaimed tokens");
        bool transferSuccess = saleToken.transfer(to, available);
        require(transferSuccess, "Reclaim transfer failed");
        emit UnclaimedTokensReclaimed(to, available);
    }

    /**
     * @dev Rescue any stuck ERC20 tokens (not saleToken) or Lumia sent by mistake.
     *      Only admin can call.
     */
    function rescueTokens(
        address token,
        uint256 amount,
        address to
    ) external onlyRole(DEFAULT_ADMIN_ROLE) nonReentrant {
        require(to != address(0), "Invalid recipient");
        if (token == address(0)) {
            // Rescue LUMIA
            require(address(this).balance >= amount, "Not enough LUMIA");
            (bool sent, ) = to.call{value: amount}("");
            require(sent, "LUMIA rescue failed");
        } else {
            require(token != address(saleToken), "Cannot rescue saleToken");
            require(
                IERC20(token).balanceOf(address(this)) >= amount,
                "Not enough tokens"
            );
            bool transferSuccess = IERC20(token).transfer(to, amount);
            require(transferSuccess, "ERC20 rescue transfer failed");
        }
        emit Rescue(token, amount, to);
    }

    /**
     * @dev Receive and fallback - revert to prevent accidental LUMIA sending.
     */
    receive() external payable {
        revert("Direct LUMIA not accepted");
    }

    fallback() external payable {
        revert("Fallback not accepted");
    }

    /**
     * @dev Finalize offering early if soft cap is reached. Only token owner can call.
     */
    function finalizeOfferingSoftCap() external onlyRole(TOKEN_OWNER_ROLE) {
        require(totalRaised >= softCap, "Soft cap not reached");
        require(!isOfferingFinalized, "Already finalized");
        require(!isOfferingCancelled, "Offering is cancelled");
        require(!isSaleClosed, "Sale already closed");

        isOfferingFinalized = true;
        isSaleClosed = true;

        // Set first payout date in WrappedToken
        if (apyEnabled && wrappedTokenAddress != address(0)) {
            IWRAPEDTOKEN(wrappedTokenAddress).setFirstPayoutDate();
        }

        emit OfferingFinalized(block.timestamp);
        emit SaleClosed(totalRaised);
    }
    /**
     * @dev Check if offering can be finalized (end date reached and not already finalized).
     */
    function canFinalize() external view returns (bool) {
        return
            (block.timestamp >= endDate || totalRaised >= softCap) &&
            !isOfferingFinalized &&
            !isOfferingCancelled;
    }

    /**
     * @dev Check if soft cap has been reached.
     */
    function isSoftCapReached() external view returns (bool) {
        return totalRaised >= softCap;
    }

    /**
     * @dev Check if offering can be cancelled by token owner.
     */
    function canCancel() external view returns (bool) {
        return !isOfferingFinalized && !isOfferingCancelled;
    }

    /**
     * @dev Get offering status information.
     */
    function getOfferingStatus()
        external
        view
        returns (
            bool saleActive,
            bool saleClosed,
            bool finalized,
            bool cancelled,
            bool softCapReached,
            uint256 raised,
            uint256 softCapAmount,
            uint256 cap,
            uint256 endTime
        )
    {
        saleActive =
            block.timestamp >= startDate &&
            block.timestamp < endDate &&
            !isSaleClosed &&
            !isOfferingCancelled;
        saleClosed = isSaleClosed;
        finalized = isOfferingFinalized;
        cancelled = isOfferingCancelled;
        softCapReached = totalRaised >= softCap;
        raised = totalRaised;
        softCapAmount = softCap;
        cap = fundraisingCap;
        endTime = endDate;
    }
}

contracts/OfferingFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "./Offering.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "./interfaces/IWrappedTokenFactory.sol";

// Import the WrapedTokenConfig struct
import {WrappedTokenConfig} from "./structs/WrappedTokenConfig.sol";

struct CreateOfferingConfig {
    address saleToken;
    uint256 minInvestment;
    uint256 maxInvestment;
    uint256 startDate;
    uint256 endDate;
    bool apyEnabled;
    uint256 softCap;
    uint256 fundraisingCap;
    uint256 tokenPrice;
    address tokenOwner;
    address escrowAddress;
    address investmentManager;
    address payoutTokenAddress;
    uint256 payoutRate;
    uint256 payoutPeriodDuration;
    uint256 maturityDate;
}

/**
 * @title OfferingFactory
 * @dev Factory contract to deploy and manage Offering contracts.
 */
contract OfferingFactory is Ownable {
    uint256 public count;
    mapping(uint256 => address) public offerings;
    mapping(address => address) public owners;
    mapping(address => uint256[]) public byOwner;

    // USDT configuration
    address public usdtAddress;
    address public usdtOracleAddress;

    // WrappedTokenFactory reference
    IWrappedTokenFactory public wrappedTokenFactory;

    event OfferingDeployed(
        uint256 indexed offeringId,
        address indexed creator,
        address indexed offeringAddress,
        address tokenOwner
    );

    event USDTConfigUpdated(
        address indexed usdtAddress,
        address indexed usdtOracleAddress
    );

    event WrappedTokenFactoryUpdated(
        address indexed oldFactory,
        address indexed newFactory
    );

    constructor(address _wrappedTokenFactory) Ownable(msg.sender) {
        require(_wrappedTokenFactory != address(0), "Invalid factory");
        wrappedTokenFactory = IWrappedTokenFactory(_wrappedTokenFactory);
    }

    function setWrappedTokenFactory(
        address _wrappedTokenFactory
    ) external onlyOwner {
        require(_wrappedTokenFactory != address(0), "Invalid factory");
        address oldFactory = address(wrappedTokenFactory);
        wrappedTokenFactory = IWrappedTokenFactory(_wrappedTokenFactory);
        emit WrappedTokenFactoryUpdated(oldFactory, _wrappedTokenFactory);
    }

    function setUSDTConfig(
        address _usdtAddress,
        address _usdtOracleAddress
    ) external onlyOwner {
        require(_usdtAddress != address(0), "Invalid USDT address");
        require(_usdtOracleAddress != address(0), "Invalid oracle address");

        usdtAddress = _usdtAddress;
        usdtOracleAddress = _usdtOracleAddress;

        emit USDTConfigUpdated(_usdtAddress, _usdtOracleAddress);
    }

    function _generateWrappedTokenNames(
        address saleToken
    ) internal view returns (string memory name, string memory symbol) {
        // Generate name from sale token
        try IERC20Metadata(saleToken).name() returns (string memory tokenName) {
            name = string(abi.encodePacked(tokenName, " Wrapped"));
        } catch {
            name = "Wrapped Token"; // Fallback name
        }

        // Generate symbol from sale token
        try IERC20Metadata(saleToken).symbol() returns (
            string memory tokenSymbol
        ) {
            symbol = string(abi.encodePacked("w", tokenSymbol));
        } catch {
            symbol = "WRT"; // Fallback symbol
        }
    }

    function createOffering(
        CreateOfferingConfig memory config
    ) external onlyOwner returns (address offeringAddress) {
        require(config.escrowAddress != address(0), "Invalid escrow address");
        require(
            config.payoutTokenAddress != address(0),
            "Invalid payout token address"
        );

        address wrappedTokenAddress = address(0);

        Offering offering = new Offering();
        if (config.apyEnabled) {
            // Generate wrapped token names
            (
                string memory wrappedName,
                string memory wrappedSymbol
            ) = _generateWrappedTokenNames(config.saleToken);

            WrappedTokenConfig memory wrappedConfig = WrappedTokenConfig({
                name: wrappedName,
                symbol: wrappedSymbol,
                peggedToken: config.saleToken,
                payoutToken: config.payoutTokenAddress,
                maturityDate: config.maturityDate,
                payoutAPR: config.payoutRate,
                offeringContract: address(offering),
                admin: msg.sender,
                payoutPeriodDuration: config.payoutPeriodDuration
            });
            wrappedTokenAddress = wrappedTokenFactory.createWrappedToken(
                wrappedConfig
            );
        }

        InitConfig memory initConfig = InitConfig({
            saleToken: config.saleToken,
            minInvestment: config.minInvestment,
            maxInvestment: config.maxInvestment,
            startDate: config.startDate,
            endDate: config.endDate,
            softCap: config.softCap,
            fundraisingCap: config.fundraisingCap,
            tokenPrice: config.tokenPrice,
            tokenOwner: config.tokenOwner,
            escrowAddress: config.escrowAddress,
            apyEnabled: config.apyEnabled,
            wrappedTokenAddress: wrappedTokenAddress,
            investmentManager: config.investmentManager,
            payoutTokenAddress: config.payoutTokenAddress,
            payoutRate: config.payoutRate
        });

        offering.initialize(initConfig);
        offeringAddress = address(offering);

        // Register the offering with the Escrow contract
        IEscrow(config.escrowAddress).registerOffering(
            offeringAddress,
            config.tokenOwner
        );

        _storeOffering(offeringAddress, config.tokenOwner);
    }

    function createOfferingWithPaymentTokens(
        CreateOfferingConfig memory config,
        address[] memory paymentTokens,
        address[] memory oracles
    ) external onlyOwner returns (address offeringAddress) {
        require(
            paymentTokens.length == oracles.length,
            "Array length mismatch"
        );
        require(paymentTokens.length > 0, "No payment tokens provided");
        require(config.escrowAddress != address(0), "Invalid escrow address");
        require(
            config.payoutTokenAddress != address(0),
            "Invalid payout token address"
        );

        address wrappedTokenAddress = address(0);
        Offering offering = new Offering();

        if (config.apyEnabled) {
            // Generate wrapped token names
            (
                string memory wrappedName,
                string memory wrappedSymbol
            ) = _generateWrappedTokenNames(config.saleToken);

            WrappedTokenConfig memory wrappedConfig = WrappedTokenConfig({
                name: wrappedName,
                symbol: wrappedSymbol,
                peggedToken: config.saleToken,
                payoutToken: config.payoutTokenAddress,
                maturityDate: config.maturityDate,
                payoutAPR: config.payoutRate,
                offeringContract: address(offering),
                admin: msg.sender,
                payoutPeriodDuration: config.payoutPeriodDuration
            });
            wrappedTokenAddress = wrappedTokenFactory.createWrappedToken(
                wrappedConfig
            );
        }

        InitConfig memory initConfig = InitConfig({
            saleToken: config.saleToken,
            minInvestment: config.minInvestment,
            maxInvestment: config.maxInvestment,
            startDate: config.startDate,
            endDate: config.endDate,
            softCap: config.softCap,
            fundraisingCap: config.fundraisingCap,
            tokenPrice: config.tokenPrice,
            tokenOwner: config.tokenOwner,
            escrowAddress: config.escrowAddress,
            apyEnabled: config.apyEnabled,
            wrappedTokenAddress: wrappedTokenAddress,
            investmentManager: config.investmentManager,
            payoutTokenAddress: config.payoutTokenAddress,
            payoutRate: config.payoutRate
        });

        offering.initialize(initConfig);
        offeringAddress = address(offering);

        // Register the offering with the Escrow contract
        IEscrow(config.escrowAddress).registerOffering(
            offeringAddress,
            config.tokenOwner
        );

        _configurePaymentTokens(offering, paymentTokens, oracles);
        _storeOffering(offeringAddress, config.tokenOwner);
    }

    function _configurePaymentTokens(
        Offering offering,
        address[] memory paymentTokens,
        address[] memory oracles
    ) internal {
        for (uint256 i = 0; i < paymentTokens.length; i++) {
            if (paymentTokens[i] != address(0)) {
                require(
                    oracles[i] != address(0),
                    "Invalid oracle for ERC20 token"
                );
            } else {
                require(
                    oracles[i] != address(0),
                    "Invalid oracle for native ETH"
                );
            }
            offering.setTokenOracle(paymentTokens[i], oracles[i]);
            offering.setWhitelistedPaymentToken(paymentTokens[i], true);
        }
    }

    function _storeOffering(
        address offeringAddress,
        address tokenOwner
    ) internal {
        offerings[count] = offeringAddress;
        owners[offeringAddress] = msg.sender;
        byOwner[tokenOwner].push(count);

        emit OfferingDeployed(count, msg.sender, offeringAddress, tokenOwner);
        count++;
    }

    function getOfferingAddress(
        uint256 offeringId
    ) external view returns (address) {
        return offerings[offeringId];
    }

    function getOfferingOwner(
        address offeringAddress
    ) external view returns (address) {
        return owners[offeringAddress];
    }

    function getOfferingIdsByTokenOwner(
        address tokenOwner
    ) external view returns (uint256[] memory) {
        return byOwner[tokenOwner];
    }

    function getAllOfferings() external view returns (address[] memory) {
        address[] memory result = new address[](count);
        for (uint256 i = 0; i < count; i++) {
            result[i] = offerings[i];
        }
        return result;
    }

    function getUSDTConfig()
        external
        view
        returns (address usdtToken, address usdtOracle)
    {
        return (usdtAddress, usdtOracleAddress);
    }
}

